package com.codingwithjames.ekminuterukh;import android.content.Intent;import android.content.IntentFilter;import android.support.v4.view.ViewPager;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import com.google.android.gms.ads.MobileAds;public class MainActivity extends AppCompatActivity {    private ViewPager mViewPager;    private Page_Adapter adapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        mViewPager = findViewById(R.id.viewPager);        adapter = new Page_Adapter(getSupportFragmentManager());        mViewPager.setAdapter(adapter);        mViewPager.setCurrentItem(0);    }}